document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".filter-btn"),t=document.querySelectorAll(".post");e.forEach(n=>{n.addEventListener("click",()=>{const s=n.getAttribute("data-filter");e.forEach(e=>e.classList.remove("active")),n.classList.add("active"),t.forEach(e=>{console.log(e.getAttribute("filters")),console.log(e.getAttribute("filters").includes(s)),s==="all"||e.getAttribute("filters").includes(s)?e.style.display="block":e.style.display="none"})})})})