document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".filter-btn"),t=document.querySelectorAll(".post");e.forEach(t=>{t.addEventListener("click",()=>{const n=t.getAttribute("data-filter");e.forEach(e=>e.classList.remove("active")),t.classList.add("active"),console.posts.forEach(e=>{n==="all"||e.getAttribute("filters").contains(n)?e.style.display="block":e.style.display="none"})})})})