document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".filter-btn"),t=document.querySelectorAll(".post");e.forEach(n=>{n.addEventListener("click",()=>{const s=n.getAttribute("data-filter");e.forEach(e=>e.classList.remove("active")),n.classList.add("active"),t.forEach(e=>{s==="all"||e.getAttribute("filters").contains(s)?e.style.display="block":e.style.display="none"})})})})